            Dictionary<string, object> encoderSettings = new Dictionary<string, object>();

            encoderSettings.Add("W", 21);  //the number of bits that are set to encode a single value -the "width" of the output signal and w must be odd.
            encoderSettings.Add("N", 389);  //The number of bits in the output. Must be greater than or equal to w we calculated with respect to formule.
            encoderSettings.Add("MinVal", (double)20);//The minimum value of the input signal we taken the real value of latitude in min we take the min value of given country.
            encoderSettings.Add("MaxVal", (double)48);//The upper bound of the input signal we taken the real value of latitude in max we take the max valuie of the given country.
            encoderSettings.Add("Radius", (double)1.5);//Two inputs separated by more than the radius have non-overlapping representations, Two inputs separated by less than the radius will in general overlap in at least of their bits. You can think of this as the radius of the input.
            encoderSettings.Add("Periodic", (bool)false);// Two inputs separated by greater than, or equal to the resolution are guarantee to have different representations.
            encoderSettings.Add("ClipInput", (bool)true);//If true, then the input value "wraps around" such that minval = maxval For a periodic value, the input must be strictly less than maxval, otherwise maxval is a true upper bound.
            encoderSettings.Add("IsRealCortexModel", false);

            GeoSpatialEncoderExperimental encoder = new GeoSpatialEncoderExperimental(encoderSettings);

            var result = encoder.Encode(input);// it use for encoding the input according to the given parameters.
